export const run = async () => {
  /* Split text */
  const text = fs.readFileSync(
    path.resolve(__dirname, "../../state_of_the_union.txt"),
    "utf8"
  );

  const splitter = new TokenTextSplitter({
    encodingName: "r50k_base",
    chunkSize: 10,
    chunkOverlap: 0,
