  [" "],
  [{ id: 1 }],
  new OpenAIEmbeddings()
);
const retriever = vectorStore.asRetriever();

// Instantiate the LLM,
const llm = new ChatOpenAI({
  modelName: "gpt-4",
});

// And the chain.
const voeChain = ViolationOfExpectationsChain.fromLLM(llm, retriever);

